import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import '../styles/NavBar.css'

export default function Header() {
  const [HosActive, setHosActive] = useState(false)
  const [ResActive, setResActive] = useState(false)
  const [CaiActive, setCaiActive] = useState(false)
  const [FinActive, setFinActive] = useState(false)


  const handleToggle = ({target}) => {
    if (target.name === 'hospedes') {
      setHosActive(true)
    }if (target.name === 'restaurante') {
      setResActive(true)
    }if (target.name === 'caixa') {
      setCaiActive(true)
    }if (target.name === 'financeiro') {
      setFinActive(true)
    }
  }

  return (
    <div className="topnav">
      <Link 
        to="/hospedes" 
        name="hospedes"
        className={!HosActive ? "active" : ""}
        onClick={(e) => handleToggle(e)}
      >
        HÃ³spedes
      </Link>
      <Link 
        to="/restaurante"
        name="restaurante"
        className={!ResActive ? "active" : ""}
        onClick={(e) => handleToggle(e)}
      >
          Restaurante
      </Link>
      <Link 
        to="/caixa"
        name="caixa"
        className={!CaiActive ? "active" : ""}
        onClick={(e) => handleToggle(e)}
      >
          Caixa
      </Link>
      <Link 
        to="/financeiro"
        name="financeiro"
        className={!FinActive ? "active" : ""}
        onClick={(e) => handleToggle(e)}
      >
          Financeiro
      </Link>
    </div>
  )
}
