import { useState, useEffect } from "react";
import { useHistory } from "react-router";
// import React, {useState, useEffect} from 'react'
import Context from "./Context";


function HostelProvider({children}) {
  const [user, setUser] = useState({
    name: '',
    email: '',
  })
  const [HosActive, setHosActive] = useState(false)
  const [ResActive, setResActive] = useState(false)
  const [CaiActive, setCaiActive] = useState(false)
  const [FinActive, setFinActive] = useState(false)

  const history = useHistory()
  const { location: { pathname } } = history

  useEffect(() => {
    const handleToggle = async () => {
      await pathname === '/hostel' ? setHosActive(true) : setHosActive(false)
      await pathname === '/restaurante' ? setResActive(true) : setResActive(false)
      await pathname === '/caixa' ? setCaiActive(true) : setCaiActive(false)
      await pathname === '/financeiro' ? setFinActive(true) : setFinActive(false)
    }
    handleToggle()
  }, [pathname])

  const context = {
    user, 
    setUser,
    HosActive,
    ResActive,
    CaiActive,
    FinActive
  }
  return (
    <Context.Provider value={ context }>
      {children}
    </Context.Provider>
  )
}

export default HostelProvider
