import React, { useState, useEffect } from 'react'
import { Link, useHistory } from 'react-router-dom'
import '../styles/NavBar.css'

export default function Header() {
  const [HosActive, setHosActive] = useState(false)
  const [ResActive, setResActive] = useState(false)
  const [CaiActive, setCaiActive] = useState(false)
  const [FinActive, setFinActive] = useState(false)

  const history = useHistory()
  const { location: { pathname } } = history

  const handleToggle = () => {
    if (pathname === '/hospedes') {
      setHosActive(true)
    }if (pathname === '/restaurante') {
      setResActive(true)
    }if (pathname === '/caixa') {
      setCaiActive(true)
    }if (pathname === '/financeiro') {
      setFinActive(true)
    }
  }

  useEffect(() => {
      pathname === '/hospedes' ? setHosActive(true) : setHosActive(false)
  }, [pathname])

  return (
    <div className="topnav">
      <Link 
        to="/hospedes" 
        name="hospedes"
        className={HosActive ? "active" : ""}
        onClick={() => handleToggle()}
      >
        HÃ³spedes
      </Link>
      <Link 
        to="/restaurante"
        name="restaurante"
        className={ResActive ? "active" : ""}
        onClick={() => handleToggle()}
      >
          Restaurante
      </Link>
      <Link 
        to="/caixa"
        name="caixa"
        className={CaiActive ? "active" : ""}
        onClick={() => handleToggle()}
      >
          Caixa
      </Link>
      <Link 
        to="/financeiro"
        name="financeiro"
        className={FinActive ? "active" : ""}
        onClick={() => handleToggle()}
      >
          Financeiro
      </Link>
    </div>
  )
}
