// }, [pathname])

  return (
    <div>
      <Header />
      <NavBar />
      <div>
        <ul className="rooms">
          {rooms.map((room) => (
            <Link to={`/hostel/${room.name}`}>
              <button>
                <FaBed className="bed"/>
                <li key={room.id}>{room.name}</li>
                <div className="hr"></div>
              </button>
            </Link>
            ))
          }
        </ul>
      </div>
    </div>
  )
}
